//init datepicker
function initDatepicker() {
	//wrapper functions to make datepicker initialization easier
    function dateBeginningOfDay(date) {
	    date.setHours(0,0,0);
		return date;
	}
	function dateEndOfDay(date) {
		date.setHours(23,59,59);
		return date;
	}
	function parseDateTime(dateString) {
		date = jQuery.datepicker.parseDateTime(date_format, 'HH:mm', dateString);
		return date;
	}
	function getStartDate() {
		dateString = jQuery('#from').attr('value') + ' ' + jQuery('#time1').attr('value');
		return parseDateTime(dateString);
	}
	function getEndDate() {
		dateString = jQuery('#till').attr('value') + ' ' + jQuery('#time2').attr('value');
		return parseDateTime(dateString);
	}
	function setDateTimePickerDate(element, date) {
		element.datetimepicker('disable');
		element.datetimepicker('setDate', date);
		element.datetimepicker('enable');
	}

	jQuery('.calendar-holder').each(function(){
		var set = jQuery(this);
		var checkin = set.find('.checkin');
		var checkout = set.find('.checkout');
		var startDate = false;
		var endDate = false;
		var value;
		var value2;
		function calcTop(input) {
			var row = jQuery(input).parent();
			value = row.outerHeight() + row.offset().top;
			value2 = row.offset().left;
		}
		checkin.datetimepicker({
			minDate: new Date(2005, 1, 1),
			maxDate: dateEndOfDay(new Date()),
			altField: "#time1",
			dateFormat: date_format,
			timeFormat: "HH:mm",
			stepMinute: 1,

			onSelect: function(dateText, inst){
				startDate = new Date(dateText);
			},
			onClose: function( selectedDate ) {
			    checkout.datetimepicker( "option", "minDate", selectedDate );
			},
			beforeShowDay: function(date){
				calcTop(checkin);
				setTimeout(function () {
					jQuery('#ui-datepicker-div').css({
						top: value,
						left: value2
					});
				}, 0);
				return [true, startDate && ((date.getTime() == startDate.getTime()) || (endDate && date >= startDate && date <= endDate)) ? "dp-highlight" : ""];
			}
		});
		checkout.datetimepicker({
			dateFormat: date_format,
			altField: "#time2",
			timeFormat: "HH:mm",
			stepMinute: 1,

			onSelect: function(dateText, inst){
				endDate = new Date(dateText);
			},
			onClose: function( selectedDate ) {
				checkin.datetimepicker( "option", "maxDate", selectedDate );
			},
			beforeShowDay: function(date){
				calcTop(checkout);
				setTimeout(function () {
					jQuery('#ui-datepicker-div').css({
						top: value,
						left: value2
					});
				}, 0);
				return [true, startDate && ((date.getTime() == startDate.getTime()) || (endDate && date >= startDate && date <= endDate)) ? "dp-highlight" : ""];
			}
		});
		checkinCalendar = [checkin.parent().find('.calendar-link'), checkin.parent().parent().find('.clock-link')];
		jQuery.each(checkinCalendar, function(){
			jQuery(this).click(function() {

				checkin.trigger('select');
			});
		});
		checkoutCalendar = [checkout.parent().find('.calendar-link'), checkout.parent().parent().find('.clock-link')];
		jQuery.each(checkoutCalendar, function(){
			jQuery(this).click(function() {
				checkout.trigger('select');
			});
		});
		setDateTimePickerDate(checkin, getStartDate());
		setDateTimePickerDate(checkout, getEndDate());
	})
}

// checked classes when element active
function initCheckedClasses() {
	var checkedClass = 'input-checked', parentCheckedClass = 'input-checked-parent';
	var pairs = [];
	jQuery('label[for]').each(function(index, label) {
		var input = jQuery('#'+label.htmlFor);
		label = jQuery(label);

		// click handler
		if(input.length) {
			pairs.push({input:input,label:label});
			input.bind('click change', function() {
				if(input.is(':radio')) {
					jQuery.each(pairs, function(index, pair) {
						refreshState(pair.input, pair.label);
					});
				} else {
					refreshState(input, label);
				}
			});
			refreshState(input, label);
		}
	});

	// refresh classes
	function refreshState(input, label) {
		if(input.is(':checked')) {
			input.parent().addClass(parentCheckedClass);
			label.addClass(checkedClass);
		} else {
			input.parent().removeClass(parentCheckedClass);
			label.removeClass(checkedClass);
		}
	}
}

