			var topHeight = 0;
			var bottomHeight = 0;
			var winHeight = jQuery(window).height();
			var content_height = 0;

			if ($('.content-box').width() < 768) {
				$('.content-box').children().each(function(){
					content_height += $(this).outerHeight(true);
				});
			} else {
				$('.content-box').children().each(function(){
					col_height = $(this).outerHeight(true);
					content_height = col_height > content_height ? col_height : content_height;
				});
			}

			for(var i = 0 ; i < this.topBoxes.length; i++) {
				topHeight += this.topBoxes.eq(i).outerHeight()
			}

			for(var j = 0 ; j < this.bottomBoxes.length; j++) {
				bottomHeight += this.bottomBoxes.eq(j).outerHeight()
			}

			var boxHeight = winHeight - topHeight - bottomHeight - (this.box.outerHeight() - this.box.height()) - this.options.extraHeight*2;

			boxHeight = boxHeight > content_height ? boxHeight : content_height
